mapper.select:
SELECT id, evaluator_id, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE id = #{id}

SELECT id, evaluator_id, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE canteen_id = #{canteenId}

SELECT id, evaluator_id, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE evaluator_id = #{evaluatorId} AND canteen_id = #{canteenId}

mapper.update:
UPDATE canteen_review
SET evaluator_id = #{evaluatorId},
    canteen_id = #{canteenId},
    evaluation_score = #{evaluationScore},
    weight = #{weight}
WHERE id = #{id}
