mapper.select:
SELECT id, evaluator_id, name, time, comment, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE id = #{id}

SELECT id, evaluator_id, name, time, comment, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE canteen_id = #{canteenId}

SELECT id, evaluator_id, name, time, comment, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE evaluator_id = #{evaluatorId} AND canteen_id = #{canteenId}

SELECT id, evaluator_id, name, time, comment, canteen_id, evaluation_score, weight
FROM canteen_review
WHERE name = #{name}
