mapper.select:
SELECT id, sender_id, receiver_id, send_time, message_content
FROM chat_message
WHERE id = #{id}

SELECT id, sender_id, receiver_id, send_time, message_content
FROM chat_message
WHERE sender_id = #{senderId} AND receiver_id = #{receiverId}

mapper.update:
UPDATE chat_message
SET sender_id = #{senderId},
receiver_id = #{receiverId},
send_time = #{sendTime},
message_content = #{messageContent}
WHERE id = #{id}
