mapper.select:
SELECT id, sender_id, receiver_id, send_time, timestamp, message_content
FROM chat_message
WHERE id = #{id}

SELECT receiver_id
FROM chat_message
WHERE sender_id = #{senderId}

SELECT id, sender_id, receiver_id, send_time, timestamp, message_content
FROM chat_message
WHERE sender_id = #{senderId} AND receiver_id = #{receiverId}

mapper.update:
UPDATE chat_message
sender_id = #{senderId}, receiver_id = #{receiverId}, send_time = #{sendTime}, timestamp = #{timestamp} message_content = #{messageContent},
WHERE id = #{id}
