mapper.select:
SELECT id, publisher_id, name, publish_time, publish_timestamp, title, content, likes, comments, hot,reply_message_id, parent_id
FROM community_message
WHERE id = #{id}

SELECT id, publisher_id, name, publish_time, publish_timestamp, title, content, likes, comments, hot,reply_message_id, parent_id
FROM community_message
WHERE publisher_id = #{publisherId}

SELECT id, publisher_id, name, publish_time, publish_timestamp, title, content, likes, comments, hot,reply_message_id, parent_id
FROM community_message
WHERE content = #{Content}

SELECT id, publisher_id, name, publish_time, publish_timestamp, title, content, likes, comments, hot,reply_message_id, parent_id
FROM community_message
WHERE reply_message_id = #{replyMessageId}

SELECT id, publisher_id, name, publish_time, publish_timestamp, title, content, likes, comments, hot,reply_message_id, parent_id
FROM community_message
WHERE parent_id = #{parentId}

SELECT id, publisher_id, name, publish_time, publish_timestamp, title, content, likes, comments, hot,reply_message_id, parent_id
FROM community_message
WHERE name = #{name}

mapper.update:
UPDATE community_message
publisher_id = #{publisherId}, name = #{name}, publish_time = #{publishTime}, publish_timestamp = #{publishTimestamp}, title = #{title}, content = #{content}, likes = #{likes}, comments = #{comments}, hot = #{hot}, reply_message_id = #{replyMessageId}, parent_id = #{parentId},
WHERE id = #{id}
