mapper.select:
SELECT id, complainant_id, canteen_name, complain_time, complaint_info, feedback, response_info
FROM complaint
WHERE id = #{id}

SELECT id, complainant_id, canteen_name, complain_time, complaint_info, feedback, response_info
FROM complaint
WHERE complainant_id = #{complainantId}

SELECT id, complainant_id, canteen_name, complain_time, complaint_info, feedback, response_info
FROM complaint
WHERE canteen_name = #{canteenName}

mapper.update:
UPDATE complaint
complainant_id = #{complainantId}, canteen_name = #{canteenName}, complain_time = #{complainTime}, complaint_info = #{complaintInfo}, feedback = #{feedback}, response_info = #{responseInfo},
WHERE id = #{id}
