mapper.select:
SELECT id, canteen_id, name, image, introduction, price, cuisine, score, weight, recommend FROM dish


        AND name LIKE CONCAT('%', #{dishName}, '%')


        AND canteen_id = #{canteenId}


        AND cuisine = #{cuisine}


        AND price BETWEEN #{minPrice} AND #{maxPrice}





        AND 1=1

SELECT id, canteen_id, name, image, introduction, price, cuisine, score, weight, recommend
FROM dish
WHERE recommend = #{recommend}

mapper.update:
UPDATE dish
name = #{name}, image = #{image}, introduction = #{introduction}, price = #{price}, cuisine = #{cuisine}, score = #{score}, weight = #{weight}, recommend = #{recommend}
WHERE id = #{id}
