mapper.select:
SELECT id, evaluator_id, name, dish_id, time, timestamp, comment, evaluation_score, weight
FROM dish_review
WHERE id = #{id}

SELECT id, evaluator_id, name, dish_id, time, timestamp, comment, evaluation_score, weight
FROM dish_review
WHERE name = #{name}

mapper.update:
UPDATE dish_review
evaluator_id = #{evaluatorId}, name = #{name}, dish_id = #{dishId}, time = #{time}, time = #{timestamp}, comment = #{comment}, evaluation_score = #{evaluationScore}, weight = #{weight},
WHERE id = #{id}
