mapper.select:
SELECT id, evaluator_id, dish_id, evaluation_score, weight
FROM dish_review
WHERE id = #{id}

SELECT id, evaluator_id, dish_id, evaluation_score, weight
FROM dish_review
WHERE dish_id = #{DishId}

SELECT id, evaluator_id, dish_id, evaluation_score, weight
FROM dish_review
WHERE evaluator_id = #{evaluatorId} AND dish_id = #{dishId}

mapper.update:
UPDATE dish_review
SET evaluator_id = #{evaluatorId},
    dish_id = #{dishId},
    evaluation_score = #{evaluationScore},
    weight = #{weight}
WHERE id = #{id}
