mapper.select:
SELECT id, question_id, option_content, selection_count
FROM options
WHERE id = #{id}

SELECT id, question_id, option_content, selection_count
FROM options
WHERE question_id = #{questionId}

mapper.update:
UPDATE option
question_id = #{questionId}, option_content = #{optionContent}, selection_count = #{selectionCount},
WHERE id = #{id}
