mapper.select:
SELECT id, question_id, option_content, selection_count
FROM option
WHERE id = #{id}

SELECT id, question_id, option_content, selection_count
FROM option
WHERE question_id = #{questionId}

mapper.update:
UPDATE option
SET question_id = #{questionId},
option_content = #{optionContent},
selection_count = #{selectionCount}
WHERE id = #{id}
