mapper.select:
SELECT id, login_times, evaluation_times, community_interaction_times, level
FROM ordinary_user
WHERE id = #{id}

mapper.update:
UPDATE ordinary_user

    login_times = #{loginTimes},
    evaluation_times = #{evaluationTimes},
    community_interaction_times = #{communityInteractionTimes},

    level = #{level}

WHERE id = #{userId}
