mapper.select:
SELECT id, survey_content, publisher_id, publish_time
FROM survey
WHERE id = #{id}

SELECT id, survey_content, publisher_id, publish_time
FROM survey
WHERE publisher_id = #{publisherId}

mapper.update:
UPDATE survey
SET survey_content = #{surveyContent},
    publisher_id = #{publisherId},
    publish_time = #{publishTime}
WHERE id = #{id}
