mapper.update:
UPDATE user
username = #{username}, phone_number = #{phoneNumber}, password = #{password}, role = #{role},
WHERE id = #{id}

mapper.select:
SELECT id, username, password, phone_number, role
FROM user
WHERE phone_number = #{phoneNumber}
  AND password = #{password}

SELECT id, username, password, phone_number, role
FROM user
WHERE id = #{id}

SELECT id, username, password, phone_number, role FROM user
AND id in
#{id}

SELECT id, username, password, phone_number, role
FROM user
WHERE phone_number = #{phoneNumber}

SELECT id, username, password, phone_number, role
FROM user
WHERE username = #{username}
